import * as yup from "yup";

const SignupSchema = yup.object().shape({
  firstName: yup.string()
    .min(2, 'Too Short!')
    .max(50, 'Too Long!')
    .required('Обязательное поле'),
  email: yup.string().email('Invalid email').required('Обязательное поле'),
});

export default SignupSchema
