import { useState } from 'react'
import * as React from "react";
import Header from "./Header/Header";
import Main from "./Main/Main";
import { get } from 'lodash'


const users = {
  messages: [
    {
      content: "Сообщение 1",
      sender_id: "mnzxcv97zx6chvo",
      uuid: "dg897sdfg"
    },
    {
      content: "Сообщение 2",
      sender_id: "98s7dfh9a8s7dhf",
      uuid: "8723hernm"
    },
    {
      content: "Еще одно сообщение",
      sender_id: "mnzxcv97zx6chvo",
      uuid: "435nbcv98234"
    }
  ],
  me: {
    is_online: true,
    name: 'Алексей',
    uuid: '98s7dfh9a8s7dhf',
  },
  members: [
    {
      is_online: true,
      name: 'Алексей',
      uuid: '98s7dfh9a8s7dhf',
    },
    {
      is_online: true,
      name: 'Дмитрий',
      uuid: 'mnzxcv97zx6chvo',
    },
    {
      is_online: false,
      name: 'Андрей',
      uuid: 'kjuhv987ashdfoua',
    },
    {
      is_online: false,
      name: 'Владимир',
      uuid: 'jdhnf978WEHJSNDL',
    },
  ],
}


const ChatPage = () => {
  const [state, setState] = useState(users)

  const onSendNewMessage = async (message:string) => {
    console.log(message)
  }

  const onUpdateVisitor = (visitor: any) => {
    setState(visitor)
  }
  return (
    <div>
      <Header me={get(state, 'me')} updateVisitor={onUpdateVisitor} />
      <Main members={state.members} me={state.me} messages={state.messages} onSendNewMessage={onSendNewMessage} />
    </div>
  )
}

export default ChatPage
