import { FC, useState } from 'react'
import * as React from "react"

import RoomTitle from "../RoomTitle/RoomTitle"

import styles from './header.module.scss'

interface IChatHeader {
  me: {
    is_online: boolean
    name: string
    uuid: string
  }
  updateVisitor: (visitor: { name: string; is_online: boolean; uuid: string }) => void
}

const Header: FC<IChatHeader> = ({ me, updateVisitor }) => {
  const [name, setName] = useState(me.name ? me.name : me.uuid.substr(-10))
  const handleChangeName = (e: any) => {
    setName(e.target.value)
    const visitor = { ...me }
    visitor.name = e.target.value
    updateVisitor(visitor)
  }
  return (
    <div className={styles.titleInput}>
      <RoomTitle />
      {me ? (
        <>
          <input
            className={styles.name_input}
            value={name}
            placeholder="Ваше имя"
            onChange={e => handleChangeName(e)}
          />
        </>
      ) : null}
    </div>
  )
}

export default Header
