import React, {useState} from 'react'
import { Field, Form, Formik } from 'formik'

import SignupSchema from './yup'
import axios from 'axios'
import {get} from 'lodash'

const FormikFirst = () => {
  const [submittingStatus, setSubmittingStatus] = useState(false)
  const [errorBack, setErrorBack] = useState()

  return (
    <Formik
      initialValues={{
      email: '',
      password: '',
    }}
      validationSchema={SignupSchema}
      onSubmit={values => {
        setErrorBack(null)
        if (submittingStatus) {
          return
        }
        setSubmittingStatus(true)
        axios
          .post('http://localhost:8000/auth/signUp', {email: values.email, password: values.password})
          .then((res) => {
            setSubmittingStatus(false)
          })
          .catch((err) => {
            setErrorBack(get(err, 'response.status'))
            setSubmittingStatus(false)
          })
      }}
    >
      {({ errors, touched, handleChange }) => (
        <Form>
          <Field
            type="text"
            name="email"
            placeholder="Email"
            onChange={handleChange}
          /><br />
          {errors.email && touched.email ? (
            <div>{errors.email}</div>
          ) : null}
          {errorBack === 403 && <div>Данный email уже зарегистрирован</div>}
          <Field
            type="password"
            placeholder="Password"
            name="password"
            onChange={handleChange}
          /><br />
          {errorBack === 402 && <div>Пароль слишком короткий</div>}
          {errors.password && touched.password ? <div>{errors.password}</div> : null}
          <button type="submit">
            Отправить
          </button>
        </Form>
      )}
    </Formik>
  )
}

export default FormikFirst
