import React, { FC } from "react"

import MessageContainer from "../MessageContainer/MessageContainer"
import SendMessageForm from "../SendMessageForm/SendMessageForm"

// import styles from './chatField.module.scss'

interface IChatField {
  messages: {
    content: string,
    sender_id: string,
    uuid: string
  }[]
  members: {
    is_online: boolean
    name: string
    uuid: string
  }[]
  onSendNewMessage: (message: string) => void
}

const ChatField: FC<IChatField> = ({ messages, members, onSendNewMessage }) => {
  return (
    <section className="messageItem">
      <MessageContainer
        members={members}
        messages={messages}
      />
      <SendMessageForm onSendNewMessage={onSendNewMessage}/>
    </section>
  )
}

export default ChatField
