import { call, put, takeEvery } from 'redux-saga/effects'

import {SET_PROFILE_DATA, SET_LOGIN_DATA} from "../store/actions"
import useLogin from "../hooks/useLogin"
import axios from "axios";

function* fetchUser(action) {
  const getLoginData = axios
    .get('http://localhost:8000/profile')
    .then(response => {
      dispatch({
        type: SET_PROFILE_DATA,
        data: response.data.profile,
        payload: response.data.profile,
      })
    })
    .catch(() => {
    })

  try {
    const login = yield call(getLoginData, action.payload.login)
    yield put({type: SET_LOGIN_DATA, login: login})
  } catch (e) {
    yield console.log("Нет данных")
  }
}

function* mySaga() {
  yield takeEvery(SET_PROFILE_DATA, fetchUser)
}

export default mySaga
