import React from "react"
import { Field, Form, Formik } from "formik"

import SignupSchema from "./yup"
import axios from "axios";
import {get} from "lodash";

const FormikFirst = () => {

  axios
    .post('http://localhost:8000/auth/signUp', {email: email, password: passwordInputVal})
    .then((res) => {
      onSubmit(res)
      setSubmittingStatus(false)
    })
    .catch((err) => {
      setErrorBack(get(err, 'response.data.message'))
      setSubmittingStatus(false)
    });

  return (
    <Formik
      initialValues={{
      email: '',
      password: '',
    }}
      validationSchema={SignupSchema}
      onSubmit={values => {
        console.log(values)
      }}
    >
      {({ errors, touched }) => (
        <Form>
          <Field
            type="text"
            name="email"
            placeholder="Email"
          /><br />
          {errors.email && touched.email ? (
            <div>{errors.email}</div>
          ) : null}
          <Field
            placeholder="Password"
            name="password"
            type="password"
          /><br />
          {errors.password && touched.password ? <div>{errors.password}</div> : null}
          <button type="submit">
            Отправить
          </button>
        </Form>
      )}
    </Formik>
  )
}

export default FormikFirst
