import { FC, useState } from "react";
import React from "react";

interface ISendMessageForm {
  onSendNewMessage: (message: string) => void
}

const SendMessageForm: FC<ISendMessageForm> = ({ onSendNewMessage }) => {
  const [state, setState] = useState({ message: '' })

  const currentMessageChanged = (e:any) => {
    setState({message: e.target.value });
  }

  const sendMessageClicked = async (e:any) => {
    e.preventDefault();
    if (state.message.length > 0) {
      await onSendNewMessage(state.message);
      setState({...state, ...{message : ''}});
    }
  }

  return (
    <section className="SendMessageForm">
      <form>
        <input
          type="text"
          value={state.message}
          onChange={currentMessageChanged}
          placeholder="Написать сообщение"/>
        <button
          type="submit"
          onClick={sendMessageClicked}
        >
          Отправить
        </button>
      </form>
    </section>
  );
}

export default SendMessageForm
