import { FC } from 'react'
import * as React from "react";

interface IChatMemberItem {

  member: {
    is_online: boolean
    name: string
    uuid: string
  }
  me: {
    is_online: boolean
    name: string
    uuid: string
  }
}

const MemberItem: FC<IChatMemberItem> = ({ member, me }) => {
  const getName = () => {
    let name = ''
    if (member.uuid === me.uuid) {
      if (me.name) {
        name = me.name
      } else {
        name = me.uuid.substring(me.uuid.length - 10, me.uuid.length)
      }
    } else {
      if (member.name) {
        name = member.name
      } else {
        name = member.uuid.substring(member.uuid.length - 10, member.uuid.length)
      }
    }
    return name
  }

  return (
    <div className="memberItem">
      <span>
        {getName()}
        {member.uuid === me.uuid && ' (Вы) '}
      </span>
      {member.is_online && <span className="online">•</span>}
    </div>
  )
}

export default MemberItem
