import React from "react";
import * as yup from "yup";
import {Field, Form, Formik} from "formik";
import SignupSchema from "./yup";

const FormikFirst = () => {
  return (
    <Formik
      initialValues={{
      firstName: '',
      email: '',
    }}
      validationSchema={SignupSchema}
      onSubmit={values => {
        console.log(values);
      }}
    >
      {({ errors, touched }) => (
        <Form>
          <Field
            type="text"
            name="firstName"
            placeholder="Имя"
          /><br />
          {errors.firstName && touched.firstName ? (
            <div>{errors.firstName}</div>
          ) : null}
          <Field
            placeholder="Email"
            name="email"
          /><br />
          {errors.email && touched.email ? <div>{}</div> : null}
          <button type="submit">
            Отправить
          </button>
        </Form>
      )}
    </Formik>
  );
};

export default FormikFirst;
