import { put, takeEvery } from 'redux-saga/effects'
import axios from 'axios'

import { SET_PROFILE_DATA, SET_LOGIN_DATA } from '../store/actions'

function* fetchUser() {
  const p = new Promise((resolve, reject) => {
    axios
      .get('https://jsonplaceholder.typicode.com/todos/1')
      .then(r => resolve(r))
  })

  // const data = (() => {
  //   return (
  //     axios
  //       .get('https://jsonplaceholder.typicode.com/todos/1')
  //       .then(r =>r.data)
  //   )
  // })

  try {
    const login = p.then(data => data.data)
    console.log(p.then(data => data.data))
    yield put({type: SET_LOGIN_DATA, payload: login})
  } catch (e) {
    yield console.log('Нет данных')
  }
}

function* mySaga() {
  yield takeEvery(SET_PROFILE_DATA, fetchUser)
}

export default mySaga
