import { FC } from 'react'
import * as React from "react";

import MemberList from "../MemberList/MemberList";
import ChatField from "../ChatField/ChatField";

import styles from './Main.module.scss'

interface IChatMain {
  members: {
    is_online: boolean
    name: string
    uuid: string
  }[]
  me: {
    is_online: boolean
    name: string
    uuid: string
  }
  messages: {
    content: string,
    sender_id: string,
    uuid: string
  }[]
  onSendNewMessage: (message: string) => void
}

const Main: FC<IChatMain> = ({ members, me, messages, onSendNewMessage }) => {
  return (
    <section className={styles.main}>
      <MemberList members={members} me={me} />
      <ChatField onSendNewMessage={onSendNewMessage} messages={messages} members={members} />
    </section>
  )
}

export default Main
