import React, {useState} from "react";
import { useForm } from "react-hook-form";
import { get } from 'lodash'
import * as yup from "yup";

import FormFormik from "./formYup";
import useLogin from "../../hooks/useLogin";
import FormikFirst from "./firmikFirst";
import HookForm from "./hookForm";

const schema = yup.object().shape({
  name: yup.string().required(),
  email: yup.string().required(),
  emailValid: yup.string().required(),
});

const Form = () => {

  const [busyLogin, setBusyLogin] = useState(false)
  const {
    register,
    handleSubmit,
    formState: {errors, submitCount},
    watch
  } = useForm({mode: "onChange"})


  const {email, getLoginData} = useLogin()
  const onsubmit = (data) => {
    getLoginData(data)
    if (data.email === get(email, 'email')) {
      setBusyLogin(true)
    } else {
      setBusyLogin(false)
      console.log('email ', data.email, 'Годиков ', data.age)
    }
  }

  return(
    <div>
      <h1>Hook form</h1>
      <form onSubmit={handleSubmit(onsubmit)}>
        <input {...register("name")} placeholder="Имя" />
        <br />
        <input {...register('email')} placeholder="email" />
        {busyLogin && <span>Логин уже занят<br /></span>}
        <br />
        <input {...register('emailValid')} placeholder="email Valid" />
        <br />
        {busyLogin && <span>Логин уже занят<br /></span>}
        <br/>
        <input type="submit"/>
        <br />
      </form>
      Сколько раз вы отправили форму?
      {submitCount}
      <FormFormik />
      {/*<h1>Переиспользуемые формы</h1>*/}
      {/*<h2>Formik</h2>*/}
      {/*<FormikFirst />*/}
      {/*<h2>HookForm2</h2>*/}
      {/*<HookForm />*/}
    </div>
  )
}
export default Form
