const User = require('../src/js/user').default;
const expect = require('chai').expect;

describe('User', () => {

    it('should properly get/set UUID', () => {
        let user = new User();
        let testUUID = '93a75a9a-8f10-4686-ba1e-bec14438c2d1';
        user.setUuid(testUUID);
        expect(user.getUuid()).to.be.equal(testUUID);
    });

    it('should properly get/set Name', () => {
        let user = new User();
        let testName = 'test name';
        user.setName(testName);
        expect(user.getName()).to.be.equal(testName);
    });

    it('should properly get/set Email', () => {
        let user = new User();
        let testEmail = 'test@example.com';
        user.setEmail(testEmail);
        expect(user.getEmail()).to.be.equal(testEmail);
    });

    it('should properly get/set isOnline', () => {
        let user = new User();
        
        user.setIsOnline(false);
        expect(user.getIsOnline()).to.be.equal(false);
        user.setIsOnline(false);
        expect(user.getIsOnline()).to.be.equal(false);
    });

});